import React, { useState, useEffect } from 'react';
import './Right.css';

const Right = () => {
  const [percentage, setPercentage] = useState(95); // Initial percentage value

  useEffect(() => {
    // Calculate the background color based on the percentage
    const backgroundColor = `rgba(224, 224, 224, ${1 - (percentage / 100)})`;

    // Update the background color dynamically
    document.querySelector('.circle').style.backgroundColor = backgroundColor;
  }, [percentage]);

  return (
    <div className='container1'>
      <div className='con1'>
        <p className='northeast'>&#x2197;</p>
        <p className='titletext'>Material Available</p>
      </div>
      <div className='con1'>
        <p className='northeast'>&#x2197;</p>
        <p className='titletext'>Contacts</p>
        <div className='email'>
          <p className='title1'>Email</p>
        </div>
        <div className='email'>
          <p className='title1'>Phone Number</p>
        </div>
      </div>
      <div className='con1'>
        <p className='northeast'>&#x2197;</p>
        <p className='titletext'>Students Review</p>
        <div className='circle'>
          <p>{percentage}%</p>
        </div>
        {/* Manually change the percentage value */}
        <button onClick={() => setPercentage(50)}>Change Progress</button>
      </div>
    </div>
  );
};

export default Right;
