import React, { useState } from 'react';
import './Right.css';

const Right = () => {
  const [percentage, setPercentage] = useState(95); // Initial percentage value

  const updatePercentage = (newPercentage) => {
    setPercentage(newPercentage);
  };

  const calculateBackgroundColor = () => {
    // Calculate the background color based on the percentage
    return `rgba(224, 224, 224, ${1 - (percentage / 100)})`;
  };

  return (
    <div className='container1'>
      <div className='con1'>
        <p className='northeast'>&#x2197;</p>
        <p className='titletext'>Material Available</p>
      </div>
      <div className='con1'>
        <p className='northeast'>&#x2197;</p>
        <p className='titletext'>Contacts</p>
        <div className='email'>
          <p className='title1'>Email</p>
        </div>
        <div className='email'>
          <p className='title1'>Phone Number</p>
        </div>
      </div>
      <div className='con1'>
        <p className='northeast'>&#x2197;</p>
        <p className='titletext'>Students Review</p>
        <div className='circle' style={{ backgroundColor: calculateBackgroundColor() }}>
          <p>{percentage}%</p>
        </div>
        <button onClick={() => updatePercentage(50)}>Update Progress</button>
      </div>
    </div>
  );
};

export default Right;
