import React from 'react';
import Nav from './Nav/Nav';
import './Material.css';

const Material = () => {
  // Example data with multiple entries
  const detailsData = [
    {
      name: 'John Doe',
      date: '2024-03-12',
      dueDate: '2024-03-20',
      material: 'Book',
      studentId: '12345',
      description: 'Lorem ipsum',
      action: 'Read',
      additionalDetails: {
        grade: 'A',
        status: 'Completed',
        comments: 'Excellent work',
        attendance: 'Present',
        marks: '95',
      },
    },
    {
      name: 'Jane Smith',
      date: '2024-03-15',
      dueDate: '2024-03-25',
      material: 'Notebook',
      studentId: '67890',
      description: 'Dolor sit amet',
      action: 'Write',
      additionalDetails: {
        grade: 'B',
        status: 'In Progress',
        comments: 'Good effort',
        attendance: 'Absent',
        marks: '75',
      },
    },
    // Add more entries as needed
  ];

  return (
    <div className='materialContainer'>
      <Nav />
      <p className='title'>Student Details</p>
      <div className='contentContainer2'>
        <div className='infoRow'>
          <p className='name'>Name</p>
          <p className='date'>Date</p>
          <p className='duedate'>Due Date</p>
          <p className='materials'>Material</p>
          <p className='sid'>Student ID</p>
          <p className='des'>Description</p>
          <p className='action'>Action</p>
          <p className='additional'>Additional Details</p>
        </div>
        {detailsData.map((details, index) => (
          <React.Fragment key={index}>
            <div className='infoRow'>
              <p className='name'>{details.name}</p>
              <p className='date'>{details.date}</p>
              <p className='duedate'>{details.dueDate}</p>
              <p className='materials'>{details.material}</p>
              <p className='sid'>{details.studentId}</p>
              <p className='des'>{details.description}</p>
              <p className='action'>{details.action}</p>
              {/* Additional details */}
              <p className='additional'>
                Grade: {details.additionalDetails.grade}
              </p>
              <p className='additional'>
                Status: {details.additionalDetails.status}
              </p>
              <p className='additional'>
                Comments: {details.additionalDetails.comments}
              </p>
              <p className='additional'>
                Attendance: {details.additionalDetails.attendance}
              </p>
              <p className='additional'>
                Marks: {details.additionalDetails.marks}
              </p>
            </div>
            <div className='spacer' /> {/* Add a spacer to create a new line */}
          </React.Fragment>
        ))}
      </div>
    </div>
  );
};

export default Material;
