import React, { useState, useEffect } from 'react';
import './Right.css';

const Right = () => {
  const [percentage, setPercentage] = useState(95);

  useEffect(() => {
    const progressBar = document.querySelector('.circle-progress');

    // Calculate the angle based on the percentage
    const angle = (percentage / 100) * 360;

    // Apply the rotation to the circular progress bar
    progressBar.style.transform = `rotate(${angle}deg)`;

    // Apply color gradient based on the percentage
    const gradientColor = `conic-gradient(#00ff00 0% ${angle}%, transparent ${angle}% 100%)`;
    progressBar.style.backgroundImage = gradientColor;
  }, [percentage]);

  // Example: Change the percentage dynamically
  const handlePercentageChange = () => {
    // Change this value based on your logic
    setPercentage(70);
  };

  return (
    <div className='container1'>
      <div className='con1'>
        <p className='northeast'>&#x2197;</p>
        <p className='titletext'>Material Available</p>
      </div>
      <div className='con1'>
        <p className='northeast'>&#x2197;</p>
        <p className='titletext'>Contacts</p>
        <div className='email'>
          <p className='title1'>Email</p>
        </div>
        <div className='email'>
          <p className='title1'>Phone Number</p>
        </div>
      </div>
      <div className='con1'>
        <p className='northeast'>&#x2197;</p>
        <p className='titletext'>Students Review</p>
        <div className='circle'>
          <div className='circle-progress'></div>
          <p>{percentage}%</p>
        </div>
      </div>

      {/* Example: Button to change percentage dynamically */}
      <button onClick={handlePercentageChange}>Change Percentage</button>
    </div>
  );
};

export default Right;
