import React, { useState } from 'react';
import Nav from './Nav/Nav';
import './Auth.css';

const Signup = () => {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = (event) => {
    event.preventDefault();
    // Add logic to handle signup submission using the entered data (username, email, password)
  };

  return (
    <div className='SignupContainer'>
      <Nav />
      <div className='DetailContainer'>
        <h1>Signup</h1>
        <form onSubmit={handleSubmit}>
          <label>Username</label>
          <input type='text' value={username} onChange={(e) => setUsername(e.target.value)} />

          <label>Email</label>
          <input type='email' value={email} onChange={(e) => setEmail(e.target.value)} />

          <label>Password</label>
          <input type='password' value={password} onChange={(e) => setPassword(e.target.value)} />

          {/* You can add more input fields for additional signup information */}
          
          <button type="submit">Submit</button>
        </form>
        <p className="noaccount"><a href='/login'>Already have an account? Login here</a></p>
      </div>
    </div>
  );
};

export default Signup;
